/**
 * n8n Code node ‚Äî Run Once for All Items
 * This version:
 *  - DOES NOT check keyword match
 *  - ONLY filters schedules where startDateObj > today
 *  - Groups schedules per user by area_code
 *  - Removes duplicated schedules by 100%-match text (hash)
 */

const NODE_USERS   = "Get Active Users";
const NODE_RESULTS = "Results";

// ------------------ Helper: normalize ------------------
function normalizeStr(s) {
  if (!s || typeof s !== "string") return "";
  return s
    .normalize("NFD")
    .replace(/[ÃÄ-ÕØ]/g, "")
    .toLowerCase()
    .trim();
}

// ------------------ Load USERS ------------------
let usersItems = [];
try {
  usersItems = $items(NODE_USERS);
} catch (e) {
  throw new Error(`Kh√¥ng t√¨m th·∫•y node "${NODE_USERS}".`);
}

// ------------------ Load RESULTS ------------------
let results = $input.all().map(i => i.json);

if (!results.length) {
  try {
    const r = $items(NODE_RESULTS).map(i => i.json);
    results = r;
  } catch (e) {}
}

if (!results.length) {
  throw new Error("Kh√¥ng c√≥ d·ªØ li·ªáu l·ªãch c√∫p ƒëi·ªán.");
}

// ------------------ Output ------------------
const userForMailing = [];

const today = new Date();

// ------------------ Process ------------------
for (const user of usersItems) {

  const userArea = user?.json?.area_code || "";

  // T·∫°o object output
  let entry = {
    user: { ...user.json },
    lich_cup: []
  };

  for (const rec of results) {

    // Match theo area_code
    if ((rec?.area_code || "") !== userArea) continue;

    let startDate = rec?.time_raw?.[0] || "";

    // Extract ph·∫ßn text n·∫±m gi·ªØa "ng√†y ... ƒë·∫øn"
    const m = startDate.match(/ng√†y(.*?)ƒë·∫øn/i);
    if (m && m[1]) {
      startDate = m[1].trim();
    }

    rec.startDate = startDate;

    // Convert dd/mm/yyyy ‚Üí Date object
    const parts = startDate.split("/");
    if (parts.length === 3) {
      const [d, m, y] = parts.map(n => parseInt(n.trim(), 10));
      if (!isNaN(d) && !isNaN(m) && !isNaN(y)) {
        rec.startDateObj = new Date(y, m - 1, d);
      }
    }

    // Ch·ªâ l·∫•y l·ªãch t∆∞∆°ng lai
    if (!(rec.startDateObj instanceof Date)) continue;
    if (rec.startDateObj <= today) continue;

    // Build hash 100% text match
    const hash =
      normalizeStr(rec.where?.[0] || "") +
      "|" +
      normalizeStr(rec.time_raw?.[0] || "");

    // Push t·∫°m, s·∫Ω l·ªçc tr√πng sau
    entry.lich_cup.push({
      ...rec,
      hash
    });
  }

  // ------------------------
  // üî• KH·ª¨ TR√ôNG 100% TEXT
  // ------------------------
  const seen = new Set();
  const uniqueList = [];

  for (const item of entry.lich_cup) {
    if (!seen.has(item.hash)) {
      seen.add(item.hash);
      uniqueList.push(item);
    }
  }

  entry.lich_cup = uniqueList;

  // ------------------------
  // Ch·ªâ push n·∫øu user c√≤n l·ªãch
  // ------------------------
  if (entry.lich_cup.length > 0) {
    userForMailing.push(entry);
  }
}

return userForMailing;

