// Helper: t√¨m xem l·ªãch ƒë√£ t·ªìn t·∫°i trong sheet ch∆∞a
function isSentBefore(userId, hash) {
  return historyRows.some(
    row =>
      String(row.user_id).trim() === String(userId).trim() &&
      String(row.hash).trim() === String(hash).trim()
